xml.script:
'use strict';
$(($, w = window, d = document, c = console)=>{
	$('.photo').find('a').on('click', function(e){
		e.preventDefault();
		$('#overlay').show().addClass('image').find('img')[0].src = this.href;
	});
	$('[data-embed]').on('click', function(e){
		e.preventDefault();
		$('#overlay').show().addClass('embed');
		$('#_embed_').contents().clone().attr('src', 'https://' + this.dataset.embed).appendTo('#overlay>.content');
		$('#overlay').find('iframe').hide().on('load', function(e){
			$(this).show();
		});
		$('#overlay').find('.content').attr('style', 'background-image: url(' + $(this).find('img')[0].src + ')');
		$('#overlay').find('.open')[0].href = this.href;
	});
	$('#overlay').on('click', function(e){
		if(e.target != this) return;
		$(this).hide().find('iframe').remove();
	});
});
