'use strict';
					var log = {'textContent': document.childNodes[1].data};
					console.log('%c' + log.textContent, 'color: #;');
					window.addEventListener('error', function(e){(document.getElementById('log')||log).textContent += '\r\n' + (e.message ? e.message : e.target.nodeName + ' | ' + e.target.src);}, true);
