media.xml.script:
'use strict';
	(function load($ = window.jQuery, w = window, d = document, c = console){
		if(!$) return Object.defineProperty(window, 'jQuery', {'set': _=>load(_)});
		if(w.location.search == '?NOSCRIPT') return d.title = '[NOSCRIPT] ' + d.title;
		$(w).on('hashchange', function(e){
			let page = d.getElementById(w.location.hash.slice(1));
			d.title = $(page).hasClass('p-page') ? page.title + ' | ' + d.head.title : d.head.title;
			w.requestAnimationFrame(_=>w.setTimeout(_=>$('#pages').find('iframe').remove()));
		}).trigger('hashchange');
		$('#_search_').on('submit', function(e){
			e.preventDefault();
			w.location.assign(this.action);
			$(d.getElementById(this.action.split('#')[1])).find('input')[0].value = this.q.value;
			this.q.value = '';
		}).find('input').removeAttr('disabled').on('keyup', function(e){
			if(typeof(e.code) != 'undefined') return;
			w.location.assign($(this.list).find('option')[this.value.split('.')[0]-1].dataset.href);
			this.value = '';
		});
		$('#_theme_').removeAttr('disabled').find('input').on('click', function(e){
			d.body.classList = this.value;
		});
		if('setResourceTimingBufferSize' in w.performance){
			w.performance.setResourceTimingBufferSize(d.images.length);
			$('img.youtube').one('load', function(e){
				w.performance.getEntriesByName(this.src, 'resource').forEach(_=>{if(_.decodedBodySize == 1097) this.src = 'data:,';});
			}).each(function(){
				if(this.complete) $(this).trigger('load');
			});
		}
		$('[data-embed]').on('click', function(e){
			switch(true) {
				case this.hostname == 'fb.com':
					$('#_iframe_').contents().clone().attr('srcdoc', '<!DOCTYPE html>' + $('#_fbcontainer_')[0].innerHTML.replace('about:blank', this.dataset.embed)).insertBefore(this);
					break;
				case this.hostname == 'instagr.am':
					$('#_iframe_').contents().clone().attr('srcdoc', '<!DOCTYPE html>' + $('#_igcontainer_')[0].innerHTML.replace('about:blank', this.dataset.embed)).insertBefore(this);
					break;
				case this.hostname == 'tf1.fr':
					$('#_iframe_').contents().clone().attr('srcdoc', '<!DOCTYPE html>' + $('#_tfcontainer_')[0].innerHTML.replace('about:blank', this.dataset.embed)).insertBefore(this);
					break;
				case this.hostname == 'noodlemagazine.com':
					$('#_iframe_').contents().clone().attr('srcdoc', '<!DOCTYPE html>' + $('#_nmcontainer_')[0].innerHTML.replace('about:blank', this.dataset.embed)).insertBefore(this);
					break;
				case this.pathname.endsWith('.mp3') || this.pathname.endsWith('.mp4'):
					$('#_iframe_').contents().clone().attr('srcdoc', '<!DOCTYPE html>' + $('#_player_')[0].innerHTML.replace('about:blank', this.dataset.embed)).insertBefore(this);
					break;
				default:
					$('#_iframe_').contents().clone().attr('src', this.dataset.embed).insertBefore(this);
			}
			return false;
		});
		$('#pages').find('b, i').on('click', function(e){
			if(this.hasAttribute('data-text')) return;
			this.dataset.text = '⌛';
			let {port1, port2} = new MessageChannel();
			port1.onmessage = ({data})=>{
				this.dataset.text = data;
				port1.close();
			};
			$('#translate')[0].contentWindow.postMessage(this.textContent, '*', [port2]);
		});
		$('[data-time]').on('click', function(e){
			let i = $(this).parent().parent().find('iframe')[0];
			if(!i) return;
			let x = this.dataset.time.split(':').reduce((x,y)=>60*x+1*y);
			switch(i.nextElementSibling.hostname) {
				case 'youtu.be':
					i.contentWindow.postMessage('{"event":"command","func":"seekTo","args":["' + x + '"]}', '*');
					break;
				case 'fb.com':
					i.contentWindow.postMessage({'xdArbiterHandleMessage': true, 'message': {'method': 'seek', 'params': '{"target":' + x + '}'}}, '*');
					break;
				case 'dai.ly':
					i.contentWindow.postMessage('{"command":"seek","parameters":[' + x + ']}', '*');
					break;
			}
		});
		$(d.getElementById('/search')).find('form').on('submit', function(e){
			e.preventDefault();
			if(this.q.value.length < this.q.minLength) return;
			this.q.value = this.q.value.toLowerCase();
			$(this).parent().find('ol').empty();
			let xpath = '';
			if(this.accents.checked){
				let accents = 'çéëïàèùâêîôû', normal = accents.normalize('NFD').replace(/\p{Diacritic}/gu, '');
				this.q.value = this.q.value.normalize('NFD').replace(/\p{Diacritic}/gu, '');
				xpath = `//*[@class="text"][contains(translate(translate(., "${accents.toUpperCase() + accents}", "${normal + normal}"), "${this.q.value.toUpperCase()}", "${this.q.value}"), "${this.q.value}")]`;
			} else {
				xpath = `//*[@class="text"][contains(translate(., "${this.q.value.toUpperCase()}", "${this.q.value}"), "${this.q.value}")]`;
			}
			try {
				let node, nodes = [], result = d.evaluate(xpath, d.body, null, XPathResult.ANY_TYPE, null);
				while(node = result.iterateNext()){
					nodes.push(node);
				}
				$(this).parent().find('span')[0].textContent = nodes.length + ' results:';
				nodes.forEach(node=>{
					$(this).parent().find('ol').append(`<li>${node.textContent}</li>`);
				});
			} catch(error) {
				$(this).parent().find('ol').append(`<li>${error}</li>`);
			}
		});
	})();
