music.xml.script:
'use strict';
	(function load($ = window.jQuery, w = window, d = document, c = console){
		if(!$) return Object.defineProperty(window, 'jQuery', {'set': _=>load(_)});
		if(w.location.search == '?NOSCRIPT') return d.title = '[NOSCRIPT] ' + d.title;
		let icon = $('link')[0].href;
		$(w).on('hashchange', function(e){
			let page = d.getElementById(w.location.hash.slice(1));
			d.title = $(page).hasClass('page') ? page.title + ' | ' + d.head.title : d.head.title;
			$('link')[0].href = $(page).find('.cover').attr('src') || icon;
		}).trigger('hashchange');
		(function player(temp){
			$(w).on('message', function({'originalEvent': {'data': d}}){
				c.log(d);
				$('#log')[0].textContent += ' | ' + d;
				switch(true){
					case d == 'READY':
						$('body').removeClass('loading').find('a.active').click();
						break;
					case d == 'UNSTARTED' && temp == 'BUFFERING':
						$('#iframe').addClass('active'), c.log('!!!');
						break;
					case d == 'ENDED' || d == 'ERROR':
						$('.active').removeClass('active'), c.groupEnd();
						break;
					case d == 'PLAYING':
						$('#iframe').removeClass('active');
						let $p = $('.lyrics.active');
						let t = $p.data('time')||[];
						(function line(i = 0){
							c.log(i);
							$('#log')[0].textContent += i + '>';
							if(i > 0){
								$p.find('.active').removeClass('active');
								$p.find('li').eq(i-1).addClass('active');
							}
							if(i < t.length) w.setTimeout(line, (t[i]-(t[i-1]||0))*1000, i+1);
						})();
						break;
				}
				temp = d;
			});
			$('.page').find('.play').on('click', function(e){
				e.preventDefault();
				$('.active').removeClass('active');
				$(this).addClass('active');
				if(!!$('#iframe')[0].contentDocument){
					$('body').addClass('loading');
					let host = ['urlecho.appspot.com/echo?X-XSS-Protection=0&body=', 'public-firing-range.appspot.com/reflected/parameter/head?q='][1];
					$('#iframe')[0].src = 'https://' + host + encodeURIComponent($('#iframe')[0].name.trim().replace(/\s\/\/.*/g, '').replace(/\s+/g, ' '));
				} else {
					c.groupEnd(), c.groupCollapsed('%c' + this.textContent, 'font-weight: bold;');
					$('#log')[0].textContent += '\r\n' + this.textContent;
					$('#iframe')[0].contentWindow.postMessage({'video': this.href.split('/').pop()}, '*');
					if($(this).next()[0].dataset.time) $(this).next().addClass('active');
				}
			});
		})();
		$('#_search_').on('submit', function(e){
			e.preventDefault();
			w.location.assign(this.action);
			$(d.getElementById(this.action.split('#')[1])).find('input')[0].value = this.q.value;
			this.q.value = '';
		}).find('input').removeAttr('disabled').on('keyup', function(e){
			if(typeof(e.code) != 'undefined') return;
			w.location.assign($(this.list).find('option[value="' + this.value + '"]')[0].dataset.href);
			this.value = '';
		});
		$(d.getElementById('/search')).find('form').on('submit', function(e){
			e.preventDefault();
			if(this.q.value.length < this.q.minLength) return;
			this.q.value = this.q.value.toLowerCase();
			$(this).parent().find('ol').empty();
			let xpath = '';
			if(this.accents.checked){
				let accents = 'çéëïàèùâêîôû', normal = accents.normalize('NFD').replace(/\p{Diacritic}/gu, '');
				this.q.value = this.q.value.normalize('NFD').replace(/\p{Diacritic}/gu, '');
				xpath = `//*[@class="lyrics"]//*[@lang="fr"][contains(translate(translate(., "${accents.toUpperCase() + accents}", "${normal + normal}"), "${this.q.value.toUpperCase()}", "${this.q.value}"), "${this.q.value}")]`;
			} else {
				xpath = `//*[@class="lyrics"]//*[@lang="fr"][contains(translate(., "${this.q.value.toUpperCase()}", "${this.q.value}"), "${this.q.value}")]`;
			}
			try {
				let node, nodes = [], result = d.evaluate(xpath, d.body, null, XPathResult.ANY_TYPE, null);
				while(node = result.iterateNext()){
					nodes.push(node);
				}
				$(this).parent().find('.count')[0].textContent = nodes.length + ' Results for ' + this.q.value;
				nodes.forEach(node=>{
					$(this).parent().find('ol').append(`<li><b><a href="#${node.closest('.page').id}">${node.closest('.page').title}</a></b>${node.textContent.replaceAll(this.q.value, '<mark>$&</mark>')}</li>`);
				});
			} catch(error) {
				$(this).parent().find('ol').append(`<li>${error}</li>`);
			}
		});
	})();
