template.script:
'use strict';
((w=window,d=document,s,m,p)=>{
	d.body.append((s=d.createElement('script'),s.src='//youtube.com/iframe_api',s));
	w.onYouTubeIframeAPIReady=e=>{
		m=_=>w.parent.postMessage(_,'*');
		p=new YT.Player('p',{
			host:w.location.protocol+'//www.youtube-nocookie.com',
			playerVars:{
				controls:0,
				widget_referrer:'youtu.be'
			},
			events:{
				onReady:e=>m('READY'),
				onStateChange:e=>m(Object.entries(YT.PlayerState).find(_=>_[1]==e.data)[0]),
				onError:e=>m('ERROR')
			}
		});
	};
	w.onmessage=({data:{video:v}})=>v&&p.loadVideoById(v);
})();

xml.script:
'use strict';
	$(($, w = window, d = document, c = console, _ = {'title': d.title})=>{
		_.icon = $('link')[0].href;
		$(w).on('hashchange', function(e){
			d.title = $(':target')[0]?.dataset.title ? $(':target')[0].dataset.title + ' | ' + _.title : _.title;
			$('link')[0].href = $(':target').find('.cover')[0]?.src || _.icon;
		}).trigger('hashchange').on('message', function({'originalEvent': {'data': d}}){
			c.log(d);
			if(d == 'READY') $('body').removeClass('loading').find('a.active').click();
			if(d == 'UNSTARTED' && _.temp == 'BUFFERING') $('#iframe').addClass('active'), c.log('!!!');
			if(d == 'ENDED' || d == 'ERROR') $('.active').removeClass('active');
			if(d == 'PLAYING'){
				$('#iframe').removeClass('active');
				let $p = $('.lyrics.active');
				let t = $p.data('time')||[];
				(function line(i = 0){
					c.log(i);
					if(i > 0){
						$p.find('.active').removeClass('active');
						$p.find('li').eq(i-1).addClass('active');
					}
					if(i < t.length) w.setTimeout(line, (t[i]-(t[i-1]||0))*1000, i+1);
				})();
			}
			_.temp = d;
		});
		$('#_search_').on('submit', function(e){
			e.preventDefault();
			w.location.assign(this.action);
			$(this.getAttribute('action')).find('input')[0].value = this.q.value;
			this.q.value = '';
		}).find('input').removeAttr('disabled').on('keyup', function(e){
			if(typeof(e.code) != 'undefined') return;
			w.location.assign($(this.list).find('option[value="' + this.value + '"]')[0].dataset.href);
			this.value = '';
		});
		$('.page').find('.play').on('click', function(e){
			e.preventDefault();
			$('.active').removeClass('active');
			$(this).addClass('active');
			if($('#iframe')[0].src == 'about:blank'){
				$('body').addClass('loading');
				let x = $('#iframe').next()[0].innerHTML.trim().replace(/\s\/\/.*/g, '').replace(/\s+/g, ' ');
				let xx = ['https://urlecho.appspot.com/echo?X-XSS-Protection=0&body=', 'https://public-firing-range.appspot.com/reflected/parameter/head?q='][1];
				$('#iframe')[0].src = xx + encodeURIComponent('<!DOCTYPE html>' + x);
			} else {
				c.log('%c' + this.textContent, 'font-weight: bold;');
				$('#iframe')[0].contentWindow.postMessage({'video': this.href.split('/').pop()}, '*');
				if($(this).next().data('time')?.length) $(this).next().addClass('active');
			}
		});
		$('#search').find('form').on('submit', function(e){
			e.preventDefault();
			if(this.q.value.length < this.q.minLength) return;
			this.q.value = this.q.value.toLowerCase();
			$(this).parent().find('ol').empty();
			let xpath = '';
			if(this.accents.checked){
				let accents = 'çéëïàèùâêîôû';
				let normal = accents.normalize('NFD').replace(/\p{Diacritic}/gu, '');
				this.q.value = this.q.value.normalize('NFD').replace(/\p{Diacritic}/gu, '');
				xpath = `//*[@lang="fr"][contains(translate(translate(., "${accents.toUpperCase() + accents}", "${normal + normal}"), "${this.q.value.toUpperCase()}", "${this.q.value}"), "${this.q.value}")]`;
			} else {
				xpath = `//*[@lang="fr"][contains(translate(., "${this.q.value.toUpperCase()}", "${this.q.value}"), "${this.q.value}")]`;
			}
			try {
				let node = null;
				let nodes = [];
				let result = d.evaluate(xpath, d.body, null, XPathResult.ANY_TYPE, null);
				while(node = result.iterateNext()){
					nodes.push(node);
				}
				$(this).parent().find('span')[0].textContent = nodes.length + ' results:';
				nodes.forEach(node=>{
					$(this).parent().find('ol').append('<li><b>' + node.closest('.page').dataset.title + '</b>' + node.textContent + '</li>');
				});
			} catch(error) {
				$(this).parent().find('ol').append('<li>' + error + '</li>');
			}
		});
	});
